CREATE SCHEMA IF NOT EXISTS prod;
DROP TABLE IF EXISTS prod.competence_cross_areas;
CREATE TABLE prod.competence_cross_areas
(
    id            integer NOT NULL,
    geom          geometry(LineString, 4326),
    "name"        character varying,
    "description" character varying,
    "timestamp"   timestamp without time zone,
    "begin"       timestamp without time zone,
    "end"         timestamp without time zone,
    altitude_mode character varying,
    tessellate    integer,
    extrude       integer,
    visibility    integer,
    draw_order    integer,
    icon          character varying
);


INSERT INTO prod.competence_cross_areas (id, geom, "name", "description", "timestamp", "begin", "end", altitude_mode,
                                         tessellate, extrude, visibility, draw_order, icon)
VALUES (1,
